mapper.update:
 		update leports set review_cnt = review_cnt +1,
 		    review_star =
				(select trunc(avg(review_star),1) review_star from reservation_review
				 where leports_id = #{leports_id}
				 group by leports_id)
 		where leports_id = #{leports_id}

update leports set review_cnt = review_cnt -1,
	review_star =
	   (select trunc(avg(review_star),1) review_star from reservation_review
		where leports_id = #{leports_id}
		group by leports_id)
where leports_id = #{leports_id}
